			this.checkMobile();
			this.checkPlaceholder();
			this.fullHeight();
			this.menuHover();
			this.stickyMenu();
			this.responsiveMenu();
			this.responsiveMenuExtra();
			this.searchInput();
			this.itemSizeFix();
			this.hoverItemFix();
			this.verticalTabHeightFix();
			this.filterColorBg();
			this.productCarousel();
